placeholder="الايميل - البريد الالكترونى "
component={ InputForm }
/>
</fieldset>



<fieldset>
  <Field 
type="tel"
name="phone" 
id="phone" 
class="input-text"
placeholder="رقم الهاتف"
component={ InputForm }
/>
</fieldset>


<fieldset>
