import {ADD_ITEM_TO_CART} from '../types/cartTypes';

const AddItemToCart = item =>{
 return dispatch =>{
 	
 dispatch({
        type: ADD_ITEM_TO_CART, 
        item: item
      });
   
   
} 
 
}
