import Style from './signUp.css';
import { withRouter } from 'react-router-dom';
import * as RouterDom from 'react-router-dom';
import Googleicon from './img/googleicon.png';
import FacebookIcon from './img/Facebookicon.png';
import React, { Component } from 'react';
import { reduxForm, Field } from 'redux-form';
import { connect } from 'react-redux';
import { compose } from 'redux';
import InputForm from '../inputForm.js';
import * as actions from '../../../store/actions/users.js';
import GoogleLogin from 'react-google-login';
import FacebookLogin from 'react-facebook-login';

class signUp extends Component {

  constructor(props) {
    super(props);
    this.onSubmit = this.onSubmit.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this)
    this.responseGoogle = this.responseGoogle.bind(this);
    this.responseFacebook = this.responseFacebook.bind(this);

  }

  async onSubmit(formData) {


    const { signUp } = this.props;
    await signUp(formData);

    this.props.history.push('/dashboard')

  }

  async responseGoogle(res) {
    await this.props.oauthGoogle(res.accessToken);
    if (!this.props.errorMessage) {
      this.props.history.push('/');
    }
  }

  async responseFacebook(res) {
    await this.props.oauthFacebook(res.accessToken);
    if (!this.props.errorMessage) {
      this.props.history.push('/');
    }
  }

  async   handleSubmit(event) {
    alert(event.name)
    console.log("Hello")
    event.preventDefault()
  }
  responseGoogle(response) {
    console.log(response);
  }

  render() {
    const { handleSubmit } = this.props;
    return (

      <div class="main-container-signup">
        <div class="header">

        </div>







        <div class="form-container-signup">
          <div class="logo-pic-signup">
          </div>
          <h3 class="signup-title"> انشاء حساب جديد  </h3>
          <form onSubmit={handleSubmit(this.onSubmit)}>


            <fieldset>
              <Field
                type="text"
                name="name"
                id="name"
                class="input-text"
                placeholder="الاسم الثلاثي"
                component={InputForm}
              />
            </fieldset>


            <fieldset>
              <Field
                type="text"
                name="email"
                id="email"
                class="input-text"
                placeholder="الايميل - البريد الالكترونى "
                component={InputForm}
              />
            </fieldset>



            <fieldset>
              <Field
                type="tel"
                name="phone"
                id="phone"
                class="input-text"
                placeholder="رقم الهاتف"
                component={InputForm}
              />
            </fieldset>


            <fieldset>
              <Field
                type="password"
                name="password"
                id="password"
                class="input-text"
                placeholder="اكتب الرقم السري"
                component={InputForm}
              />
            </fieldset>

            <fieldset>
              <Field
                type="password"
                name="repassword"
                id="repassword"
                class="input-text"
                placeholder="أعد كتابة الرقم السري"
                component={InputForm}
              />
            </fieldset>




            <button type="submit" class="btn btn-primary shadow w50px grad">تسجيل</button>
          </form>


          <h5 > أو تسجيل الدخول عن طريق حسابك فيسبوك أو جوجل</h5>
          <FacebookLogin
            appId=""
            textButton="Facebook"
            fields="name,email,picture"
            callback={this.responseFacebook}


          />
          <GoogleLogin
            clientId="746252017489-f5c1v2vlrlhum6vrl2epec0t74qccbvi.apps.googleusercontent.com"
            buttonText="Google"
            onSuccess={this.responseGoogle}
            onFailure={this.responseGoogle}

          />
          <div class="social-icons">
            <img src={FacebookIcon} class="fbicon"
              height="50"
              width="50"
            />
            <img src={Googleicon} class="gicon"
              height="50"
              width="50"
            />




          </div>




        </div>

      </div>




    );
  }
}

const mapStateToProps = state => {
  return {
    errorMsg: state.userAuth.error
  }

}

export default compose(
  connect(mapStateToProps, actions),
  reduxForm({ form: 'signup' })
)(signUp)
